import { useEffect, useState } from 'react';
import './slider.css'; 

function Slider() {
  const [products, setProducts] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    fetch('/api/products')
      .then(res => res.json())
      .then(data => setProducts([...data.slice(0), ...data, ...data.slice(0, 2)])) 
      .catch(err => console.error(err));
  }, []);

  useEffect(() => {
    if (isTransitioning) {
      const handleTransitionEnd = () => {
        setIsTransitioning(false);

        if (currentIndex === products.length - 1) {
          setCurrentIndex(1);
        } else if (currentIndex === 0) {
          setCurrentIndex(products.length - 2);
        }
      };

      document.querySelector('.slider-container').addEventListener('transitionend', handleTransitionEnd);

      return () => {
        document.querySelector('.slider-container').removeEventListener('transitionend', handleTransitionEnd);
      };
    }
  }, [currentIndex, isTransitioning, products.length]);

  const nextSlide = () => {
    if (!isTransitioning) {
      setIsTransitioning(true);
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }
  };

  const prevSlide = () => {
    if (!isTransitioning) {
      setIsTransitioning(true);
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  };

  return (
    <div className="slider">
      <button className="slider-button left" onClick={prevSlide}>&#10094;</button>
      {products.length > 0 && (
        <div className="slider-container" 
             style={{ transform: `translateX(-${currentIndex * 100}%)`, transition: isTransitioning ? 'transform 0.5s ease-in-out' : 'none' }}>
          {products.map((product, i) => (
            <div key={i} className="slide">
              <img src={product.ImgPath} alt={product.Name} />
              <h2>{product.Name}</h2>
              <p>${product.Price}</p>
            </div>
          ))}
        </div>
      )}
      <button className="slider-button right" onClick={nextSlide}>&#10095;</button>
    </div>
  );
}

export default Slider;
