import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './slider.css';

const Slider = ({ products }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsToShow = 6;

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      (prevIndex + itemsToShow) % products.length
    );
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? products.length - itemsToShow : prevIndex - itemsToShow
    );
  };

  const translateValue = -(currentIndex * (100 / itemsToShow));

  return (
    <div>
      <h2>Most Common Products</h2>
      <div className="ProductItems" style={{ transform: `translateX(${translateValue}%)` }}>
        <button onClick={handlePrev} className="slider-button left">‹</button>
        <div className="ProductItemSlider">
          {products.map((product) => (
            <Link
              key={product.id}
              to={`/products/${product.id}`}
              className="product-item-link"
            >
              <div className="StoreCards">
                <img src={product.ImgPath} alt={product.Name} />
                <h3>{product.Name}</h3>
                <p>{product.Description}</p>
                <p>${product.Price}</p>
              </div>
            </Link>
          ))}
        </div>
        <button onClick={handleNext} className="slider-button right">›</button>
      </div>
    </div>
  );
};

export default Slider;
