import { useEffect, useRef, useState } from 'react';

export const useCarousel = () => {
  const wrapperRef = useRef(null);
  const carouselRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isAutoPlay, setIsAutoPlay] = useState(true);
  const [startX, setStartX] = useState(0);
  const [startScrollLeft, setStartScrollLeft] = useState(0);
  const [cardPerView, setCardPerView] = useState(0);
  const [firstCardWidth, setFirstCardWidth] = useState(0);

  useEffect(() => {
    const carousel = carouselRef.current;
    const firstCard = carousel.querySelector(".card");
    setFirstCardWidth(firstCard.offsetWidth);
    setCardPerView(Math.round(carousel.offsetWidth / firstCard.offsetWidth));

    const carouselChildren = [...carousel.children];
    carouselChildren.slice(-cardPerView).reverse().forEach(card => {
      carousel.insertAdjacentHTML("afterbegin", card.outerHTML);
    });
    carouselChildren.slice(0, cardPerView).forEach(card => {
      carousel.insertAdjacentHTML("beforeend", card.outerHTML);
    });

    carousel.classList.add("no-transition");
    carousel.scrollLeft = carousel.offsetWidth;
    carousel.classList.remove("no-transition");
  }, [cardPerView]);

  const dragStart = (e) => {
    setIsDragging(true);
    carouselRef.current.classList.add("dragging");
    setStartX(e.pageX);
    setStartScrollLeft(carouselRef.current.scrollLeft);
  };

  const dragging = (e) => {
    if (!isDragging) return;
    carouselRef.current.scrollLeft = startScrollLeft - (e.pageX - startX);
  };

  const dragStop = () => {
    setIsDragging(false);
    carouselRef.current.classList.remove("dragging");
  };

  const infiniteScroll = () => {
    const carousel = carouselRef.current;
    if (carousel.scrollLeft === 0) {
      carousel.classList.add("no-transition");
      carousel.scrollLeft = carousel.scrollWidth - (2 * carousel.offsetWidth);
      carousel.classList.remove("no-transition");
    } else if (Math.ceil(carousel.scrollLeft) === carousel.scrollWidth - carousel.offsetWidth) {
      carousel.classList.add("no-transition");
      carousel.scrollLeft = carousel.offsetWidth;
      carousel.classList.remove("no-transition");
    }

    clearTimeout(autoPlayTimeoutRef.current);
    if (!wrapperRef.current.matches(":hover")) autoPlay();
  };

  const autoPlayTimeoutRef = useRef(null);
  const autoPlay = () => {
    if (window.innerWidth < 800 || !isAutoPlay) return;
    autoPlayTimeoutRef.current = setTimeout(() => {
      carouselRef.current.scrollLeft += firstCardWidth;
    }, 2500);
  };

  useEffect(() => {
    autoPlay();
    return () => clearTimeout(autoPlayTimeoutRef.current);
  }, [isAutoPlay, firstCardWidth]);

  const handleArrowClick = (direction) => {
    carouselRef.current.scrollLeft += direction === 'left' ? -firstCardWidth : firstCardWidth;
  };

  return {
    wrapperRef,
    carouselRef,
    dragStart,
    dragging,
    dragStop,
    infiniteScroll,
    autoPlay,
    handleArrowClick
  };
};

export default useCarousel;
