import React, { useState, useEffect } from 'react';
import '../scripts_css/across.css';
import BouncyBalls from '../scripts_css/bouncyBalls';
import '../scripts_css/audioslave.css';
import Slider from './slider';
import '../scripts_css/home.css';
import Hero from '../staticImgs/image.png';

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch products from your API
    fetch('/api/products')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  return (
    <div>
      <BouncyBalls />
      <main className="home">
        <div className='Home-Div'>
          <h1 className='Slogan'>Find Your Sound: <br/> Tailored for Your Lifestyle</h1>
          <img src={Hero} alt="AudioHero" className='Hero' />
        </div>
        <section className='Slider-Section'>
          <Slider products={products} />
        </section>
      </main>
    </div>
  );
}
